<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Menu_control extends CI_Controller {    public function __construct()    {        parent::__construct();        $this->load->database();        $this->load->library('session');        $this->load->helper(array('html', 'url', 'form'));        $this->load->model('login_model');    }    public function index()    {        $data_session=$this->session->all_userdata();//Recuperar todos los Datos de Sessi√≥n        switch (isset($data_session)) {            case $data_session['rol_nombre']=='ADMINISTRADOR':                if ($data_session['hab_estado']==true)                    redirect('/Admin_control/', 'refresh');                else                    redirect('/Login_control/close_session', 'refresh');                break;            case $data_session['rol_nombre']=='TECNICO':                if ($data_session['hab_estado']==true)                    redirect('/Tec_control/', 'refresh');                else                    redirect('/Login_control/close_session', 'refresh');                break;            case $data_session ['rol_nombre'] == 'SECRETARIA':                if ($data_session ['hab_estado']==true)                    redirect('/Sec_control/', 'refresh');                else                    redirect('/Login_control/close_session', 'refresh');                break;            case $data_session['rol_nombre']=='CONSULTOR':                if ($data_session['hab_estado']==true)                    redirect('/Con_control/', 'refresh');                else                    redirect('/Login_control/', 'refresh');                break;            case $data_session['rol_nombre'] == 'REGISTRADOR':                if ($data_session ['hab_estado'] == true)                    redirect('/Reg_control/','refersh');                else                    redirect('/Login_control/', 'refresh');                break;            default:                redirect('/Login_control/close_session', 'refresh');                break;        }    }    /* Desarrollado po:        Lic. Mark Erik Copa        Ing. Nelson Erwin Aleluya        G.A.M.E.A.        2018 */}