<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Cdaf_model_tec extends CI_Model{    var $table = 'cdaf';    var $select_column = array('cdaf_id','cdaf_nro', 'cdaf_correlativo', 'cdaf_beneficiario','cdaf_estante', 'cdaf_descripcion', 'cdaf_cuce','cdaf_sisin','cdaf_adjuntar');    var $order_column =  array(null,'cdaf_nro', 'cdaf_correlativo', 'cdaf_beneficiario',null, 'cdaf_descripcion', 'cdaf_cuce','cdaf_sisin',null);    public function __construct()    {        parent::__construct();        $this->load->database();    }    function make_query()    {        $this->db->select($this->select_column);        $this->db->from($this->table);        if (isset($_POST["search"]["value"])) {            $this->db->like("cdaf_nro", $_POST["search"]["value"]);            $this->db->or_like("cdaf_correlativo", $_POST["search"]["value"]);            $this->db->or_like("cdaf_beneficiario", $_POST["search"]["value"]);            $this->db->or_like("cdaf_descripcion", $_POST["search"]["value"]);            $this->db->or_like("cdaf_cuce", $_POST["search"]["value"]);            $this->db->or_like("cdaf_sisin", $_POST["search"]["value"]);        }        if (isset($_POST["order"])) {            $this->db->order_by($this->order_column[$_POST['order']['0']['column']], $_POST['order']['0']['dir']);        } else {            $this->db->order_by("cdaf_id", "DESC");        }    }    function make_dataTables()    {        $this->make_query();        if ($_POST["length"] != -1) {            $this->db->limit($_POST["length"], $_POST["start"]);        }        $query = $this->db->get();        return $query->result();    }    function get_filtered_data()    {        $this->make_query();        $query = $this->db->get();        return $query->num_rows();    }    function get_all_data()    {        $this->db->select($this->select_column);        $this->db->from($this->table);        return $this->db->count_all_results();    }    function list_cdafComplement($cdaf_id)    {        $this->db->where('cdaf_id',$cdaf_id);        $query=$this->db->get($this->table);        return $query->row();    }    public function getCDAF($id)    {        $this->db->from('cdaf');        $this->db->where('cdaf_id', $id);        $query = $this->db->get();        return $query->row();    }    public function updateCDAF($id,$data)    {        $this->db->where('cdaf_id', $id);        $this->db->update('cdaf', $data);        if ($this->db->affected_rows() > 0)            return true;        else            return false;    }    public function uploadCDAF($data, $cdaf_id){        $this->db->where('cdaf_id', $cdaf_id);        $this->db->update('cdaf', $data);        if ($this->db->affected_rows() > 0)            return true;        else            return false;    }    public function deletePDF_CDAF($cdaf_id, $data)    {        $this->db->where('cdaf_id', $cdaf_id);        $this->db->update('cdaf', $data);        if ($this->db->affected_rows() > 0)            return true;        else            return false;    }}