<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Ci_modelo_reg extends CI_Model{    var $table = 'ci';    var $select_column = array('ci_id','ci_nro', 'ci_correlativo', 'ci_beneficiario','ci_estante', 'ci_descripcion', 'ci_cuce','ci_sisin','ci_adjuntar');    var $order_column =  array(null,'ci_nro', 'ci_correlativo', 'ci_beneficiario',null, 'ci_descripcion', 'ci_cuce','ci_sisin',null);    public function __construct()    {        parent::__construct();        $this->load->database();    }    function make_query()    {        $this->db->select($this->select_column);        $this->db->from($this->table);        if (isset($_POST["search"]["value"])) {            $this->db->like("ci_nro", $_POST["search"]["value"]);            $this->db->or_like("ci_correlativo", $_POST["search"]["value"]);            $this->db->or_like("ci_beneficiario", $_POST["search"]["value"]);            $this->db->or_like("ci_descripcion", $_POST["search"]["value"]);            $this->db->or_like("ci_cuce", $_POST["search"]["value"]);            $this->db->or_like("ci_sisin", $_POST["search"]["value"]);        }        if (isset($_POST["order"])) {            $this->db->order_by($this->order_column[$_POST['order']['0']['column']], $_POST['order']['0']['dir']);        } else {            $this->db->order_by("ci_id", "DESC");        }    }    function make_dataTables()    {        $this->make_query();        if ($_POST["length"] != -1) {            $this->db->limit($_POST["length"], $_POST["start"]);        }        $query = $this->db->get();        return $query->result();    }    function get_filtered_data()    {        $this->make_query();        $query = $this->db->get();        return $query->num_rows();    }    function get_all_data()    {        $this->db->select($this->select_column);        $this->db->from($this->table);        return $this->db->count_all_results();    }    function list_ciComplement($ci_id)    {        $this->db->where('ci_id',$ci_id);        $query=$this->db->get($this->table);        return $query->row();    }    public function saveCI($data)    {        $this->db->insert('ci', $data);        if ($this->db->affected_rows())            return true;        else            return false;    }    public function getCI($id)    {        $this->db->from('ci');        $this->db->where('ci_id', $id);        $query = $this->db->get();        return $query->row();    }    public function updateCI($id,$data)    {        $this->db->where('ci_id', $id);        $this->db->update('ci', $data);        if ($this->db->affected_rows() > 0)            return true;        else            return false;    }    public function uploadCI($data, $ci_id){        $this->db->where('ci_id', $ci_id);        $this->db->update('ci', $data);        if ($this->db->affected_rows() > 0)            return true;        else            return false;    }    public function verificar_CI_Nro($ci_nro, $ci_gestion)    {        $this->db->where('ci_nro', $ci_nro);        $this->db->where('ci_gestion', $ci_gestion);        $this->db->get('ci');        if ($this->db->affected_rows() > 0)            return false;        else            return true;    }    public function verificar_CI_Nro_Update($ci_nro, $ci_gestion)    {        $this->db->where('ci_nro', $ci_nro);        $this->db->where('ci_gestion', $ci_gestion);        $this->db->get('ci');        if ($this->db->affected_rows() > 1)            return false;        else            return true;    }    /* Desarrollado po:        Lic. Mark Erik Copa        Ing. Nelson Erwin Aleluya        G.A.M.E.A.        2018 */}