<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Dtm_model_sec extends CI_Model{    var $table = 'dtm';    var $select_column = array('dtm_id', 'dtm_nro', 'dtm_correlativo', 'dtm_beneficiario', 'dtm_estante', 'dtm_descripcion', 'dtm_cuce', 'dtm_sisin', 'dtm_adjuntar');    var $order_column = array(null, 'dtm_nro', 'dtm_correlativo', 'dtm_beneficiario', null, 'dtm_descripcion', 'dtm_cuce', 'dtm_sisin', null);    public function __construct()    {        parent::__construct();        $this->load->database();    }    function make_query()    {        $this->db->select($this->select_column);        $this->db->from($this->table);        if (isset($_POST["search"]["value"])) {            $this->db->like("dtm_nro", $_POST["search"]["value"]);            $this->db->or_like("dtm_correlativo", $_POST["search"]["value"]);            $this->db->or_like("dtm_beneficiario", $_POST["search"]["value"]);            $this->db->or_like("dtm_estante", $_POST["search"]["value"]);            $this->db->or_like("dtm_descripcion", $_POST["search"]["value"]);            $this->db->or_like("dtm_orden_cite", $_POST["search"]["value"]);            $this->db->or_like("dtm_sisin", $_POST["search"]["value"]);        }        if (isset($_POST["order"])) {            $this->db->order_by($this->order_column[$_POST['order']['0']['column']], $_POST['order']['0']['dir']);        } else {            $this->db->order_by("dtm_id", "DESC");        }    }    function make_dataTables()    {        $this->make_query();        if ($_POST["length"] != -1) {            $this->db->limit($_POST["length"], $_POST["start"]);        }        $query = $this->db->get();        return $query->result();    }    function get_filtered_data()    {        $this->make_query();        $query = $this->db->get();        return $query->num_rows();    }    function get_all_data()    {        $this->db->where('dtm_gestion', 2016);        $this->db->select($this->select_column);        $this->db->from($this->table);        return $this->db->count_all_results();    }    function list_dtmComplement($dtm_id)    {        $this->db->where('dtm_id', $dtm_id);        $query = $this->db->get($this->table);        return $query->row();    }    public function getDTM($id)    {        $this->db->from('dtm');        $this->db->where('dtm_id', $id);        $query = $this->db->get();        return $query->row();    }    public function updateDTM($id, $data)    {        $this->db->where('dtm_id', $id);        $this->db->update('dtm', $data);        if ($this->db->affected_rows() > 0)            return true;        else            return false;    }    public function deletePDF_DTM($dtm_id, $data)    {        $this->db->where('dtm_id', $dtm_id);        $this->db->update('dtm', $data);        if ($this->db->affected_rows() > 0)            return true;        else            return false;    }    public function verificar_DTM_Nro($dtm_nro, $dtm_gestion)    {        $this->db->where('dtm_nro', $dtm_nro);        $this->db->where('dtm_gestion', $dtm_gestion);        $this->db->get('dtm');        if ($this->db->affected_rows() > 0)            return false;        else            return true;    }    public function verificar_DTM_Nro_Update($dtm_nro, $dtm_gestion)    {        $this->db->where('dtm_nro', $dtm_nro);        $this->db->where('dtm_gestion', $dtm_gestion);        $this->db->get('dtm');        if ($this->db->affected_rows() > 1)            return false;        else            return true;    }}