<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Pmruc_model_tec extends CI_Model{    var $table = 'pmruc';    var $select_column = array('pmruc_id', 'pmruc_nro', 'pmruc_correlativo', 'pmruc_beneficiario', 'pmruc_estante', 'pmruc_descripcion', 'pmruc_cuce', 'pmruc_sisin', 'pmruc_adjuntar');    var $order_column = array(null, 'pmruc_nro', 'pmruc_correlativo', 'pmruc_beneficiario', null, 'pmruc_descripcion', 'pmruc_cuce', 'pmruc_sisin', null);    public function __construct()    {        parent::__construct();        $this->load->database();    }    function make_query()    {        $this->db->select($this->select_column);        $this->db->from($this->table);        if (isset($_POST["search"]["value"])) {            $this->db->like("pmruc_nro", $_POST["search"]["value"]);            $this->db->or_like("pmruc_correlativo", $_POST["search"]["value"]);            $this->db->or_like("pmruc_beneficiario", $_POST["search"]["value"]);            $this->db->or_like("pmruc_estante", $_POST["search"]["value"]);            $this->db->or_like("pmruc_descripcion", $_POST["search"]["value"]);            $this->db->or_like("pmruc_cuce", $_POST["search"]["value"]);            $this->db->or_like("pmruc_sisin", $_POST["search"]["value"]);        }        if (isset($_POST["order"])) {            $this->db->order_by($this->order_column[$_POST['order']['0']['column']], $_POST['order']['0']['dir']);        } else {            $this->db->order_by("pmruc_id", "DESC");        }    }    function make_dataTables()    {        $this->make_query();        if ($_POST["length"] != -1) {            $this->db->limit($_POST["length"], $_POST["start"]);        }        $query = $this->db->get();        return $query->result();    }    function get_filtered_data()    {        $this->make_query();        $query = $this->db->get();        return $query->num_rows();    }    function get_all_data()    {        $this->db->where('pmruc_gestion', 2016);        $this->db->select($this->select_column);        $this->db->from($this->table);        return $this->db->count_all_results();    }    function list_pmrucComplement($pmruc_id)    {        $this->db->where('pmruc_id', $pmruc_id);        $query = $this->db->get($this->table);        return $query->row();    }    public function getPMRUC($id)    {        $this->db->from('pmruc');        $this->db->where('pmruc_id', $id);        $query = $this->db->get();        return $query->row();    }    public function updatePMRUC($id, $data)    {        $this->db->where('pmruc_id', $id);        $this->db->update('pmruc', $data);        if ($this->db->affected_rows() > 0)            return true;        else            return false;    }    public function uploadPMRUC($data, $pmruc_id)    {        $this->db->where('pmruc_id', $pmruc_id);        $this->db->update('pmruc', $data);        if ($this->db->affected_rows() > 0)            return true;        else            return false;    }    public function deletePDF_PMRUC($pmruc_id, $data)    {        $this->db->where('pmruc_id', $pmruc_id);        $this->db->update('pmruc', $data);        if ($this->db->affected_rows() > 0)            return true;        else            return false;    }}