<div class="pt-16 border-bottom bd-default bg-light">    <div class="container-fluid">        <div class="row">            <div class="cell-md-9 text-center-md text-left pt-2">                <h3 class="display1">PROGRAMA DE DRENAJE PLUVIAL</h3>            </div>        </div>    </div>    <div style="overflow-x:scroll;">        <div class="container-fluid pt-0 pl-0 pr-0 pl-5-md pr-5-md">            <table class="table row-border row-hover compact" id="tablePDP">                <thead>                <tr>                    <th class="sortable-column sort-asc">PDP<br>Gestion</th>                    <th class="sortable-column sort-asc" style="width: 10%;">Nro. Correlativo/<br>Nº Fojas</th>                    <th class="sortable-column sort-asc" style="width: 10%;">Beneficiario</th>                    <th class="sortable-column sort-asc" style="width: 30%;">Descripcion</th>                    <th class="sortable-column sort-asc">Ubicacion</th>                    <th class="sortable-column sort-asc">CUCE</th>                    <th class="sortable-column sort-asc" style="width: 8%">SISIN</th>                    <th>Archivo</th>                    <th>Accion</th>                </tr>                </thead>                <tbody style="font-size: 13px;">                </tbody>            </table>        </div>    </div>    <div style="text-align: center;"><span style="text-shadow: 1px 1px 1px #3f4448; color: #ff9317; font-size: 18px;">&#174;</span>        <span style="text-shadow: 1px 1px 1px #3f4448; color: #ff9317;">G.A.M.E.A. 2018</span>    </div></div><!--modal Dialog UPLOAD PDP--><div data-role="dialog" id="demoUp" style="background-color: #f4eeb2" data-width="500">    <div class="dialog-title" style="font-weight:900;">SUBIR ARCHIVO</div>    <div class="dialog-content">        <form  id="upload" method="post" enctype="multipart/form-data" action="<?php echo site_url("Tec_control/upload_PDP");?>">            <!--El name del campo tiene que ser si o si "userfile"-->            <div class="row mb-2">                <div class="cell-md-9">                    <input type="file" data-role="file" name="pdp_file" id="pdp_file" data-validate="required" data-caption="<span class='mif-folder'></span>">                    <input type="hidden" name="nFile" id="nFile">                    <input type="hidden" name="pdp_id">                </div>                <div class="cell-md-3">                    <button type="submit" class="button primary rounded drop-shadow">Enviar Arch.</button>                </div>        </form>        <progress id="prog" max="100" value="0" style="display: none;">        </progress>        <div id="percent" style="font-weight:bold;"></div>    </div>    <div class="dialog-actions">        <button class="button alert js-dialog-close rounded drop-shadow">Cerrar</button>    </div></div></body><style type="text/css">    label{ font-size: 10px; font-weight:900;}    #tablePDP_filter{padding-left: 2%;    padding-right: 2%;}    #tablePDP_length {padding-left: 2%;        padding-right: 2%;}    #prog {        background-color :  #1a1a1a ;        height :  25px ;        padding :  5px ;        width :  350px ;        margin : 5px 11px;        border-radius :  5px ;        box-shadow :  0  1px  5px  #000  inset ,  0  1px  0  #444 ;    }    .dataTables_filter>label{        font-size: 1.2em;        color: #892a78;    }</style><script>    $(document).ready(function () {        var dataTable = $('#tablePDP').DataTable({            "processing": true,            "serverSide": true,            "order": [],            "ajax": {                url: "<?php echo base_url() . 'index.php/Tec_control/list_PDP';?>",                type: "POST"            },            "columnDefs": [                {                    "targets": [0,4,8],                    "orderable": false                }            ],            "retrieve": true,            "deferRender": true,            "crollCollapse": true,            "scroller": true,            "language": {                "sProcessing": "Procesando...",                "sLengthMenu": "Mostrar _MENU_ registros",                "sZeroRecords": "No se encontraron resultados",                "sEmptyTable": "Ningún dato disponible en esta tabla",                "sInfo": "Mostrando  _START_ al _END_ del total _TOTAL_",                "sInfoEmpty": "Mostrando registros del 0 al 0 de un total de 0 registros",                "sInfoFiltered": "(filtrado de un total de _MAX_ registros)",                "sInfoPostFix": "",                "sSearch": "Buscar:",                "sUrl": "",                "sInfoThousands": ",",                "sLoadingRecords": "Cargando...",                "oPaginate": {                    "sFirst": "Primero",                    "sLast": "Último",                    "sNext": ">>",                    "sPrevious": "<<"                },                "oAria": {                    "sSortAscending": ": Activar para ordenar la columna de manera ascendente",                    "sSortDescending": ": Activar para ordenar la columna de manera descendente"                }            },            "lengthMenu": [10, 20, 30, 100]        });        $('#pdp_monto').number( true, 2,',', '.' );        $('#e_pdp_monto').number( true, 2,',', '.' );        $('#reload').click(function () {            location.reload();        });        $('#upload').on('submit',function (e) {            e.preventDefault();            $(this).ajaxSubmit({                beforeSend:function () {                    $('#prog').show();                    $('#prog').attr('value','0');                    rowPDP()                },                uploadProgress:function (event,position,total,percentCompelete) {                    $('#prog').attr('value',percentCompelete);                    $('#percent').html(percentCompelete+'%');                },                success: function (data) {                    alertify.alert('SI-ARCH',data);                    window.setTimeout('location.reload()', 1000);                }            });        });    });    function  uploadPDP(pdp_id, pdp_nro, pdp_gestion) {        var nFile = pdp_id+'_PDP-'+pdp_nro+'_'+pdp_gestion;        $('[name="nFile"]').val(nFile );        $('[name="pdp_id"]').val(pdp_id);        Metro.dialog.open('#demoUp');    }    function  rowPDP() {    }    function deletePdfPDP(pdp_id)    {        $.ajax({            url: "<?php echo site_url('Tec_control/get_pdp/')?>" + pdp_id,            type: "GET",            dataType: "JSON",            success: function (data) {                alertify.confirm('SI-ARCH', '¿Eliminar <b>el archivo '+data.pdp_adjuntar+'</b>?', function(){                        $.ajax({                            url:  "<?php echo site_url('Tec_control/deletePdf_PDP')?>",                            type: 'POST',                            data: {"pdp_id": pdp_id, "pdp_adjuntar": data.pdp_adjuntar},                            success: function (respuesta) {                                alertify.alert('SI-ARCH',respuesta);                                window.setTimeout('location.reload()', 1000);                            },                            error: function (xhr, status) {                                alertify.error('Error al Eliminar archivo');                            }                        });                        alertify.success('Ok')                    }                    , function(){                        alertify.error('Cancel')});            },            error: function (jqXHR, textStatus, errorThrown) {                alert('Error al obtener datos de ajax');            }        })    }</script></html>