put class="text-upper" name="pre_cite_af" type="text" data-validate="required"                                       placeholder="Ej: AF/XX/201X">                                <span class="invalid_feedback">                                <span class="mif-warning"></span>Ingresar CITE A.F.</span>                            </div>                            <div class="cell-md-4">                                <label>Autorizado Por:</label>                                <select data-role="select" name="pre_autorizado" data-validate="required">                                    <?php                                    foreach ($admin as $ad) {                                        ?>                                        <option                                            value="<?php echo $ad->usu_id; ?>"><?php echo $ad->usu_nombres . ' ' . $ad->usu_paterno . ' ' . $ad->usu_materno; ?></option>                                        <?php                                    }                                    ?>                                </select>                                <span class="invalid_feedback">                                <span class="mif-warning"></span>Ingresar CITE A.F.</span>                            </div>                            <div class="cell-md-5">                                <label>observacion</label>                                <textarea class="text-upper" name="pre_observaciones"></textarea>                            </div>                        </div>                        <div class="mx-auto" style="width: 220px">                            <button class="button success large rounded drop-shadow">                                <span class="mif-file-pdf fg-white"></span> Efectuar Prestamo                            </button>                        </div>                    </form>                </div>            </div>            <div id="_target_2">                <div class="container-fluid">                    <div class="row">                        <div class="cell-md-6 text-center-md text-left pt-2">                            <h4 class="display1">LISTA DE ARCHIVOS PRESTADOS</h4>                        </div>                    </div>                </div>                <div class="container">                    <div style="overflow-y: scroll; overflow-x: scroll; height: 560px; ">                        <table class="table row-border table-border row-hover" id="MiTabla">                            <thead style="background-color: #d8d8d8;">                            <tr>                                <th style="font-size: 15px;">FUNCIONARIO</th>                                <th style="font-size: 15px;">FECHA<br>PRESTAMO</th>                                <th style="font-size: 15px;">FECHA<br>DEVOLUCION</th>                                <th style="font-size: 15px;">ARCHIVOS PRESTADOS</th>                                <th style="font-size: 15px;">DIAS DE MORA</th>                                <th style="font-size: 15px;">ACCION</th>                            </tr>                            </thead>                            <tbody style="font-size: 13px;">                            <?php                            if ($prestamos){                                foreach ($prestamos as $pre)                                {?>                                 <tr>                                     <td><span class="mif-user mif-2x"></span> <?php echo  $pre->pre_solicitante;?></td>                                     <td><span class="mif-calendar mif-2x"></span> <?php                                                                                     $pg_date = $pre->pre_fecha_prestamo;                                                                                     $date_obj = date_create_from_format('Y-m-d', $pg_date);                                                                                     $date = date_format($date_obj, 'd-m-Y');                                                                                    echo  $date;?>                                     </td>                                     <td><span class="mif-calendar mif-2x"></span> <?php                                                                                     $pg_date = $pre->pre_fecha_devolucion;                                                                                     $date_obj = date_create_from_format('Y-m-d', $pg_date);                                                                                     $date = date_format($date_obj, 'd-m-Y');                                                                                     echo  $date;?>                                     </td>                                     <td style="width:50%;">                                    <?php                                    $documentos = $this->Prestamo_model->ver_Archivo($pre->pre_id);                                    foreach ($documentos as $doc) {                                        $detalle = $this->Prestamo_model->ver_Detalle_Archivo($doc->doc_name, $doc->doc_nro, $doc->doc_gestion);?>                                        <div style="border: 2px solid gray; border-radius: 2px; margin-bottom: 4px; background-color: #e7e7e7; font-size: 11px;">                                            <b>Archivo: </b><?php echo $doc->doc_name ?>                                            <b> Nro: </b><?php echo $doc->doc_nro ?>                                            <b> Beneficiario: </b><?php echo $detalle->beneficiario ?>                                            <b> Monto: </b> Bs. <?php echo $detalle->monto ?>                                            <b>Descripcion: </b><?php echo $detalle->descripcion ?>                                        </div>                                        <?php                                    }?>                                     </td>                                     <?php                                     $hoy = date("Y-m-d");                                     if ($pre->pre_estado == 't' && $pre->pre_fecha_devolucion > $hoy) {?>                                         <td><span class="mif-calendar mif-2x fg-green"></span> <span class="mif-checkmark mif-2x fg-green"></span></td>                                     <?php }                                     else { ?>                                         <td><span class="mif-alarm mif-2x fg-red"></span><?php                                             $date1 = new DateTime($pre->pre_fecha_real_dev);                                             $date2 = new DateTime($pre->pre_fecha_devolucion);                                             $diferencia = $date2->diff($date1);                                             echo $diferencia->format('%a días');                                             ?></td>                                         <?php                                     }?>                                     <td><button class="button success drop-shadow rounded" onclick="finalizar('<?php echo $pre->pre_id ?>','<?php echo $pre->pre_solicitante ?>')" ><span class="mif-checkmark mif-1x"></span> Finalizar Prestamo</button></td>                                 </tr>                                                                        <?php                                }                            } else{                                ?>                                <tr align="center">                                    <td colspan="5"><span style="font-size: 30px; color: #7c000f;"><b> NO HAY ARCHIVOS PRESTADOS </b></span></td>                                </tr>                                <?php }?>                            </tbody>                        </table>                    </div>                </div>            </div>      